import { connect } from 'react-redux'
import _ from 'ladash'

import { readEvents } from '../actions'

class EventsIndex extends Component {

  componentDidMount(){
    this.props.readEvents()
  }


    renderEvents(){
      return _.map(this.props.events, event => (
        <tr>
          <td>{event.id}</td>
          <td>{event.title}</td>
          <td>{event.body}</td>
        </tr>
      ))
    }

