  return(
     <table>
       <thead>
         <tr>
           <th>ID</th>
           <th>Title</th>
           <th>Body</th>
         </tr>
       </thead>

       <tbody>
         {this.renderEvents()}
       </tbody>
     </table>
     )
  }
}

const mapStateToProps = state => ({ events: state.events})

const mapDispatchToProps =({ readEvents })

export default connect (mapStateToProps, mapDispatchToProps)(EventsIndex)
