import React,{Component} from 'react';

const App = () => (<Counter></Counter>)

class Counter extends Component {
  constructor(props){
    super(props)
    console.log(this.state)
    this.state = {count: 0}

  }
  render(){
  return(
    <React.Fragment>
      <div>count: {this.state.count}</div>
      <button>+1</button>
      <button>-1</button>
    </React.Fragment>
    )
  }
}


export default App;
