import _ from 'lodash'
import { 
    READ_EVENTS, READ_EVENT, DELETE_EVENTS, 
 } from '../actions'



export default (events = {}, action) =>{
    switch (action.type){
        case READ_EVENT:
            console.log(action)
        case READ_EVENTS:
            return _.mapKeys(action.response.data, 'id')
        case DELETE_EVENTS:
            delete events[action.id]
        default:
            return events
    }
}
